import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    data:'',
    hash:'',
  }
}


  render(){

    const Genesis=new BlockChain();

    const mineData=(event)=>{
      this.setState({data:event.target.value})
    }

    const onMineButton=()=>{
      Genesis.mineBlock(new Block(1,"12/06/1981","Gerald Anene"));
       Genesis.mineBlock(new Block(1,"12/06/1980","Cosmas Anene"));

       this.setState(Object.assign({},'hash',{hash:Genesis.chain[1].hash}))

      console.log(this.state.hash);
    }

    return (

    <div className="App">
      <header className="App-header">
      <h1>Block Chain</h1>

      <div className="genesis">
        <p>Genesis Block</p>
        <label>Previous Hash:<span > {Genesis.chain[0].previousHash}</span></label><br></br>
        <label>Hash:<span > {Genesis.chain[0].hash}</span></label><br></br>
        <label>Data:<span > {Genesis.chain[0].data}</span></label><br></br>
        <label>Nonce:<span > {Genesis.chain[0].nonce}</span></label>
        <hr/>
      </div>

      <div className="Blocks">
      <p>Blocks</p>
      <label>Data: <input onChange={ mineData } type="text" /></label><br></br>
      <button onClick={ onMineButton }>Mine Block</button>

      </div>




      </header>
    </div>
  );

  }

}

export default App;
