import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';

class App extends Component{
  constructor(){
    super();
     this.GenesisBlock=new BlockChain();
    this.state={
      data:'',
      chains:[this.GenesisBlock],
    }
  }

  mineData=(event)=>{
    this.setState({data:event.target.value})
  }


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} data={this.state.data} chains={this.props.chains} />
      </header>
    </div>
  );

  }

}

export default App;
