import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';

class App extends Component{
  constructor(){
    super();
    this.BlockChain=new BlockChain();

    this.state={
      data:'',
      chains:[this.BlockChain.GenesisBlock()],
      isEmptyData:true
    }
  }

  mineData=(event)=>{
    if(event.target.value.length>0){
      this.setState({data:event.target.value})
      this.setState({isEmptyData:false});
    }

  }

  mineBlock=(newBlock)=>{
    if(!this.state.isEmptyData){
          newBlock.previousHash=this.BlockChain.GetLatestBlock().hash;
          newBlock.Mine(this.BlockChain.difficulty);
          this.BlockChain.chain.push(newBlock);
          this.setState(Object.assign({},this.state,{chains:[...this.state.chains,newBlock]}));
          this.setState({data:''})

    }else{
      console.log("error")
    }


  }


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} data={this.state.data} mineBlock={this.mineBlock} chains={this.state.chains}/>
      </header>
    </div>
  );

  }

}

export default App;
