import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    chain:[],
  }
}


  render(){

    const Genesis=new BlockChain();
    // Genesis.mineBlock(new Block(1,"12/01/1923","Gerald Anene"));


    return (

    <div className="App">
      <header className="App-header">
      <h1>Block Chain</h1>

      <div className="genesis">
        <label>Previous Hash:<span > {Genesis.chain[0].previousHash}</span></label>
        <label>Hash:<span > {Genesis.chain[0].hash}</span></label>
      </div>




      </header>
    </div>
  );

  }

}

export default App;
