import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    data:{},
    blockChain:[]
  }
}

   displayresult(){
      console.log(this.state.blockChain);
    }

  render(){

    const Genesis=new BlockChain();

    const result=this.state.blockChain.filter(block=>block.data!=="Genesis Block").map((block,i)=>{
     return (
      <div>
       <label key={i}>Previous Hash:<span > {block.previousHash}</span></label><br></br>
        <label key={i}>Hash:<span > {block.hash}</span></label><br></br>
        <label key={i}>Data:<span > {block.data}</span></label>
        </div>
      )
    })


    const mineData=(event)=>{
      this.setState({data:event.target.value})
    }

    const mine=()=>{
      const block=new Block(1,"12/01/1900",this.state.data);
      Genesis.mineBlock(block);
      this.setState({blockChain:[...this.state.blockChain,Genesis.GetLatestBlock()]});
      console.log("State"+this.state.blockChain);
      console.log("Genesis"+Genesis.chain)


    }

    const mine2=()=>{
      console.log(this.state)
    }


    return (
    <div className="App">
      <header className="App-header">
      <h1>Block Chain</h1>

      <div className="genesis">
        <p>Genesis Block</p>
        <label>Previous Hash:<span > {Genesis.chain[0].previousHash}</span></label><br></br>
        <label>Hash:<span > {Genesis.chain[0].hash}</span></label><br></br>
        <label>Data:<span > {Genesis.chain[0].data}</span></label><br></br>
        <label>Nonce:<span > {Genesis.chain[0].nonce}</span></label>
        <hr/>
      </div>
      {result}
       <input type='text' onChange={ mineData } />
       <button type="submit" onClick={ mine }>Mine Block</button>
       <button type="submit" onClick={ mine2 }>Mine Block2</button>

      </header>
    </div>
  );

  }

}

export default App;
