import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';

class App extends Component{
	constructor(){
		super();
    BlockChain=new BlockChain();
		this.state={
			data:'',
      chains:[]
		}
	}

  mineData=(event)=>{
		this.setState({data:event.target.value})
	}

  mineBlock=(newBlock)=>{

    newBlock.previousHash=new BlockChain().GetLatestBlock.hash;
    newBlock.Mine(BlockChain.difficulty);
    BlockChain.chain.push(newBlock);
    this.setState(Object.assign({},this.state,{chains:[...this.state.chains,newBlock]}))
    console.log(this.state);
  }


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} data={this.state.data} mineBlock={this.mineBlock} />
      </header>
    </div>
  );

  }

}

export default App;
