import  Block  from './Block';
import React,{Component} from 'react';

 

class BlockChain extends Component{

	constructor(){
		super();
		this.chain=[this.GenesisBlock()];
	}

	GenesisBlock(){
		return new Block(0,"1/1/2020","Genesis Block",0)
	}

	GetLatestBlock(){
		return this.chain[this.chain.length-1];
	}

	MineBlock(newBlock){
		newBlock.previousHash=this.GetLatestBlock().hash;
		newBlock.hash=newBlock.calculateHash();
		this.chain.push(newBlock);
	}

	render(){
	
		const chainArray=this.chain.map((block,i)=>{
			return(
                   <Block Index={this.chain[i].Index} TransactionDate={this.chain[i].timestamp} Data={this.chain[i].data} Hash={this.chain[i].hash} PreviousHash={this.chain[i].previousHash} />
				  )

		})

		return(
			<div>
               {chainArray}
               </div>
			  )
	}

	

}

export default BlockChain;
