import Block from './Block';
import React,{ Component } from 'react';

class BlockChain extends Component{

	constructor(props){
		super(props);
		this.chain=[this.GenesisBlock()];
		this.difficulty=2;

		this.state={
			chains:[this.GenesisBlock()],
		}
	}

	GenesisBlock(){

		return new Block(0,"12/06/1923","Genesis Block","0");
	}

	GetLatestBlock(){
		return this.chain[this.chain.length-1];
	}

	mineBlock=(newBlock)=>{
		newBlock.previousHash=this.GetLatestBlock().hash;
		newBlock.Mine(this.difficulty);
		this.chain.push(newBlock);
		this.setState(Object.assign({},this.state,{chains:[...this.state.chains,newBlock]}))
		
	}
	
	render(){
		  const result=this.state.chains.filter(block=>block.data!=="Genesis Block").map(eachBlock=>{
		  	return(
                  <div key={eachBlock.hash}>
                   <label>PreviousHash:<span>{eachBlock.previousHash}</span></label><br/>
                    <label>Hash:<span>{eachBlock.hash}</span></label><br/>
                    <label>Data:<span>{eachBlock.data}</span></label><br/>
                    <label>Nonce:<span>{eachBlock.nonce}</span></label>
                    <hr/>
                  </div>
		  		  )
		  })


		return(
             <div>
	            <h1>Block Chain</h1>

			      <div className="genesis">
				        <p>Genesis Block</p>
				        <label>Previous Hash:<span > {this.chain[0].previousHash}</span></label><br/>
				        <label>Hash:<span > {this.chain[0].hash}</span></label><br/>
				        <label>Data:<span > {this.chain[0].data}</span></label><br/>
				        <label>Nonce:<span > {this.chain[0].nonce}</span></label>
				        <hr/>
			      </div>
                   
			      <h1>Other Blocks</h1>
			      <label>Enter Data <input type="text" onChange={this.props.mineData} /></label><br/>
			       { result }
			      <button onClick={()=>this.mineBlock(new Block(1,"12/06/1923",this.props.data))}>Mine Block</button>
			     <button onClick={()=>this.props.mineBlock(new Block(1,"12/06/1923",this.props.data))}>Miners</button>
		       </div>
			)
	}



}

export default BlockChain;
