import { MINE_DATA } from './constant';

const initialInputState={
   data:''
}

export const MineDataEntry=(state=initialInputState,action='')=>{
	switch(action.type){
		case MINE_DATA:
		return Object.assign({},state,{data:action.payload});

		default:
		return state;
	}
}
