import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    chain:[],
  }
}


  render(){

    const Gerald=new BlockChain();
    Gerald.mineBlock(new Block(1,"12/01/1923","Gerald Anene"));


    return (

    <div className="App">
      <header className="App-header">
      <h2>Genesis Block</h2>

      <ul>
        <label> Genesis Hash: <li>{Gerald.chain[0].hash}</li></label>
          <li>{Gerald.chain[0].previousHash}</li>
          <li>{Gerald.chain[0].data}</li>
          <li>{Gerald.chain[0].timestamp}</li>
          <li>{Gerald.chain[0].nonce}</li>
      </ul>

      </header>
    </div>
  );

  }

}

export default App;
