import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
// import Block from './Block';

class App extends Component{
	constructor(){
		this.GenesisBlock=BlockChain.GenesisBlock.bind(this);
		super();
		this.state={

			data:'',
			chains:[this.GenesisBlock()]
		}
	}

  mineData=(event)=>{

		this.setState({data:event.target.value})

	}


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} data={this.state.data} mine={this.mine} />
      </header>
    </div>
  );

  }

}

export default App;
