import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';

class App extends Component{
	constructor(){
		super();
		this.state={

			data:'',
			chains:[BlockChain.GenesisBlock]
		}
	}

  mineData=(event)=>{
		this.setState({data:event.target.value})
	}

	mineBlock=(newBlock)=>{
		newBlock.previousHash=BlockChain.GetLatestBlock.hash;
		newBlock.Mine(BlockChain.difficulty);
		BlockChain.chain.push(newBlock);
		this.setState(Object.assign({},this.state,{chains:[...this.state.chains,newBlock]}))
		console.log(this.props.data);
	}

	  result=()=>this.state.chains.filter(block=>block.data!=="Genesis Block").map(eachBlock=>{
		  	return(
                  <div key={eachBlock.hash}>
                   <label>PreviousHash:<span>{eachBlock.previousHash}</span></label><br/>
                    <label>Hash:<span>{eachBlock.hash}</span></label><br/>
                    <label>Data:<span>{eachBlock.data}</span></label><br/>
                    <label>Nonce:<span>{eachBlock.nonce}</span></label>
                    <hr/>
                  </div>
		  		  )
		  })



  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} mineBlock={this.mineBlock} data={this.state.data} result={this.result} chains={this.state.ch} />
      </header>
    </div>
  );

  }

}

export default App;
