import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();
}

      mineData=(event)=>{
      this.setState({data:event.target.value})
      }


      mine=()=>{
      const block=new Block(1,"12/01/1900",this.state.data);
      BlockChain.mineBlock(block);
      this.setState({blockChain:[...this.state.blockChain,BlockChain.GetLatestBlock()]});
    }


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} mine={this.mine} />
      </header>
    </div>
  );

  }

}

export default App;
