import Block from './Block';
import React,{ Component } from 'react';

class BlockChain extends Component{

	constructor(){
		super();
		
		this.difficulty=2;

		this.state={
			chain:[]
		}
	}

	GenesisBlock(){

		return new Block(0,"12/06/1923","Genesis Block","0");
	}

	GetLatestBlock(){
		return this.chain[this.chain.length-1];
	}

	mineBlock=(newBlock)=>{
		newBlock.previousHash=this.GetLatestBlock().hash;
		newBlock.Mine(this.difficulty);
		//this.chain.push(newBlock);
		this.setState(Object.assign({},"chain",{chain:newBlock}));
	}


	
	render(){

		// const result=this.chain.filter(block=>block.data!=="Genesis Block").map((block,i)=>{

		//   	return(
  //                  <div>
  //                   <label key={i}>Previous Hash:<span > {block.previousHash}</span></label>
  //                  </div>
		//   		  )
		//   })

		  //const result=this.chain.filter(block=>block.data!=="Genesis Block").map(item=><label key={item}>Previous Hash:<span > {item.previousHash}</span></label>);
		  const result=this.state.chain.map(item=><li>{item.hash}</li>)

		  const Genesis=this.state.filter(block=>block.data=="Genesis Block").map(eachBlock=>{
		  	return(
                   <div>
                   <label>Previous Hash:<span > {eachBlock.previousHash}</span></label><br></br>
				        <label>Hash:<span > {eachBlock.hash}</span></label><br></br>
				        <label>Data:<span > {eachBlock.data}</span></label><br></br>
				        <label>Nonce:<span > {eachBlock.nonce}</span></label>
                   </div>
		  		  )
		  })


		return(
             <div>
	            <h1>Block Chain</h1>

			      <div className="genesis">
				        <p>Genesis Block</p>
				        { Genesis }
				        <hr/>
			      </div>
                   
			      <h1>Other Blocks</h1>
			      { result }
			      <button onClick={()=>this.mineBlock(new Block(1,"12/06/1923","kclink"))}>Mine Block</button>
                 
		       </div>
			)
	}



}

export default BlockChain;
