import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';

class App extends Component{
  constructor(){
    super();
    this.state={

      data:'',
      chains:[]
    }
  }

  mineData=(event)=>{
    this.setState({data:event.target.value})
  }

  mineBlock=(newBlock)=>{
     console.log(this.state.chains);
    newBlock.previousHash=this.GetLatestBlock().hash;
    newBlock.Mine(this.difficulty);
    this.chain.push(newBlock);
    this.setState(Object.assign({},this.state,{chains:[...this.state.chains,newBlock]}))



  }


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} data={this.state.data} mineBlock={this.mineBlock}/>
      </header>
    </div>
  );

  }

}

export default App;
