import  Block  from './Block';
import React,{Component} from 'react';

 

class BlockChain extends Component{

	constructor(){
		super()
		this.chain=[this.GenesisBlock()];
	}

	GenesisBlock(){
		return new Block(0,"1/1/2020","Genesis Block",0)
	}

	GetLatestBlock(){
		return this.chain[this.chain.length-1];
	}

	MineBlock(newBlock){
		newBlock.previousHash=this.GetLatestBlock().hash;
		newBlock.hash=newBlock.calculateHash();
		this.chain.push(newBlock);
	}

	render(){

		const BlockArray=()=>{
			let gerald=new BlockChain();
			gerald.MineBlock(new Block(1,"12/06/1981","test"));
			this.chain.push(gerald);

			this.chain.map((block,i)=>{
				return <Block index={this.chain[i].index} />
			})

			console.log(gerald);

		}


		return(
               <div>
              <button onclick={this.BlockArray}>Submit</button>
               </div>
			   )
	}

}

export default BlockChain;
