import Block from './Block';
import React,{ Component } from 'react';

class BlockChain extends Component{

	constructor(){
		super();
		this.chain=[this.GenesisBlock()];
		this.difficulty=2;
	}

	GenesisBlock(){

		return new Block(0,"12/06/1923","Genesis Block","0");
	}

	GetLatestBlock(){
		return this.chain[this.chain.length-1];
	}

	mineBlock(newBlock){
		newBlock.previousHash=this.GetLatestBlock().hash;
		newBlock.Mine(this.difficulty);
		this.chain.push(newBlock);
	}

	 result=()=>this.chain.filter(block=>block.data!=="Genesis Block").map((block,i)=>{

		  	return(
                   <div>
                    <label key={i}>Previous Hash:<span > {block.previousHash}</span></label>
                   </div>
		  		  )

		  })

	render(){

		return(
             <div>
	            <h1>Block Chain</h1>

			      <div className="genesis">
				        <p>Genesis Block</p>
				        <label>Previous Hash:<span > {this.chain[0].previousHash}</span></label><br></br>
				        <label>Hash:<span > {this.chain[0].hash}</span></label><br></br>
				        <label>Data:<span > {this.chain[0].data}</span></label><br></br>
				        <label>Nonce:<span > {this.chain[0].nonce}</span></label>
				        <hr/>
			      </div>
                   
			      <h1>Other Blocks</h1>
			      <button onClick={this.result}>Mine Block</button>

		       </div>
			)
	}



}

export default BlockChain;
