import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    data:'',
    Block:[]
  }
}


  render(){

    const Genesis=new BlockChain();

    const mineData=(event)=>{
      this.setState({data:event.target.value})
    }

    const mineBlock=()=>{
       Genesis.mineBlock(new Block(2,new Date(),this.state.data));
       let newArray=[];

       Genesis.chain.map((block,i)=>{
        newArray= [...newArray,Genesis.chain[i]];
       })
       this.setState({data:''});
       this.setState({Block:newArray});
       console.log(this.state);
    }

    const BlockHashes='';

    for(i=1;i<Genesis.chain.length;i++){
     BlockHashes=<label>Hash:<span>{i.hash}</span></label>
    }
    // Genesis.chain.map(i=><label>Hash:<span>{i.hash}</span></label>)
    const BlockPreviousHashes=Genesis.chain.map(i=><label>Hash:<span>{i.previousHash}</span></label>)

    return (

    <div className="App">
      <header className="App-header">
      <h1>Block Chain</h1>

      <div className="genesis">
        <p>Genesis Block</p>
        <label>Previous Hash:<span > {Genesis.chain[0].previousHash}</span></label><br></br>
        <label>Hash:<span > {Genesis.chain[0].hash}</span></label><br></br>
        <label>Data:<span > {Genesis.chain[0].data}</span></label><br></br>
        <label>Nonce:<span > {Genesis.chain[0].nonce}</span></label>
        <hr/>
      </div>

      <div className="Blocks">
      <p>Blocks</p>
       {BlockHashes}<br></br>
       {BlockPreviousHashes}<br></br>
      <label>Data: <input onChange={ mineData } type="text" /></label><br></br>
      <button onClick={mineBlock}>Mine Block</button>

      </div>




      </header>
    </div>
  );

  }

}

export default App;
