import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';

class App extends Component{
  constructor(){
    super();
     this.GenesisBlock=new BlockChain().GenesisBlock();
     this.GetLatestBlock=this.GenesisBlock().GetLatestBlock()
    this.state={
      data:'',
      chains:[this.GenesisBlock],
    }
  }

  mineData=(event)=>{
    this.setState({data:event.target.value})
  }

  mineBlock=(newBlock)=>{
    newBlock.previousHash=this.GenesisBlock.this.GetLatestBlock.hash;
    newBlock.Mine(this.GenesisBlock.difficulty);
    this.GenesisBlock.chain.push(newBlock);
    this.setState(Object.assign({},this.state,{chains:[...this.state.chains,newBlock]}))
   console.log(this.state.chains);
  }


  render(){

    return (
    <div className="App">
      <header className="App-header">
       <BlockChain mineData={this.mineData} data={this.state.data} chains={this.props.chains} mineBlock={this.mineBlock} />
      </header>
    </div>
  );

  }

}

export default App;
