import React,{Component} from 'react';
import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    data:'',
    blockChain:[]
  }
}

     mine=()=>{
      const Genesis=new BlockChain();
      const block=new Block(1,"12/01/1900","Fidelia");
      Genesis.mineBlock(block);
      this.setState({blockChain:block});
      console.log(this.state.blockChain)
    }


  render(){

    const mineData=(event)=>{
      this.setState({data:event.target.value})
    }

    return (

    <div className="App">
      <header className="App-header">
      <h1>Block Chain</h1>

      <div className="genesis">
        <p>Genesis Block</p>

      </div>

       <button type="submit" onClick={ this.mine }>Mine Block</button>
      </header>
    </div>
  );

  }

}

export default App;
