import './App.css';
import BlockChain from './BlockChain';
import Block from './Block';

class App extends Component{

constructor(){
  super();

  this.state={
    chain:[],
  }
}


   genesisBlockData=()=>{
    const Gerald=new BlockChain();
    Gerald.mineBlock(new Block(1,"12/01/1944","Gerald Anene"))
    }

  render(){

    return (
    <div className="App">
      <header className="App-header">
      <h2>Genesis Block</h2>
      <button onClick={this.genesisBlockData}>Submit</button>
      </header>
    </div>
  );

  }

}

export default App;
